'use strict';


/**
* Test if every element in an array-like object is truthy.
*
* @module @stdlib/assert/every
*
* @example
* var every = require( '@stdlib/assert/every' );
*
* var arr = [ 1, 1, 2, 3 ];
*
* var bool = every( arr );
* // returns true
*
* bool = every( [ 0, 1, 1, 1 ] );
* // returns false
*/

// MODULES //

var every = require( './main.js' );


// EXPORTS //

module.exports = every;


