'use strict';

// MODULES //

var tape = require( 'tape' );
var signbit = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof signbit, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a boolean', function test( t ) {
	var bool;

	bool = signbit( 4.0 );
	t.equal( typeof bool, 'boolean', 'returns a boolean' );

	bool = signbit( -9.14e-307 );
	t.equal( typeof bool, 'boolean', 'returns a boolean' );

	bool = signbit( 0.0 );
	t.equal( typeof bool, 'boolean', 'returns a boolean' );

	bool = signbit( -0.0 );
	t.equal( typeof bool, 'boolean', 'returns a boolean' );

	t.end();
});

tape( 'the function returns a boolean indicating if the sign bit is on', function test( t ) {
	var bool;

	bool = signbit( 4.0 );
	t.equal( bool, false, 'returns false' );

	bool = signbit( -9.14e-307 );
	t.equal( bool, true, 'returns true' );

	bool = signbit( 0.0 );
	t.equal( bool, false, 'returns false' );

	bool = signbit( -0.0 );
	t.equal( bool, true, 'returns true' );

	t.end();
});

