'use strict';

// MODULES //

var tape = require( 'tape' );
var hoursInMonth = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof hoursInMonth, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the number of hours in a month', function test( t ) {
	var expected;
	var values;
	var i;

	values = [
		[ 2, 2016, 696 ],
		[ 2, 2017, 672 ],
		[ 1, 2016, 744 ],
		[ 1, 2017, 744 ],
		[ 12, 2015, 744 ],
		[ 12, 2016, 744 ],
		[ 3, 2015, 744 ],
		[ 3, 2016, 744 ],
		[ 4, 2015, 720 ],
		[ 4, 2016, 720 ],
		[ 5, 2015, 744 ],
		[ 5, 2016, 744 ],
		[ 6, 2015, 720 ],
		[ 6, 2016, 720 ],
		[ 7, 2015, 744 ],
		[ 7, 2016, 744 ],
		[ 8, 2015, 744 ],
		[ 8, 2016, 744 ],
		[ 9, 2015, 720 ],
		[ 9, 2016, 720 ],
		[ 10, 2015, 744 ],
		[ 10, 2016, 744 ],
		[ 11, 2015, 720 ],
		[ 11, 2016, 720 ],
		[ 0, 2015, 744 ],
		[ 0, 2016, 744 ],
		[ null, null, 744 ],
		[ 'jan', null, 744 ],
		[ 'feb', null, 744 ],
		[ 'mar', null, 744 ],
		[ 'apr', null, 720 ],
		[ 'may', null, 744 ],
		[ 'jun', null, 720 ],
		[ 'jul', null, 744 ],
		[ 'aug', null, 744 ],
		[ 'sep', null, 720 ],
		[ 'oct', null, 744 ],
		[ 'nov', null, 720 ],
		[ 'dec', null, 744 ],
		[ 'January', null, 744 ],
		[ 'February', null, 744 ],
		[ 'March', null, 744 ],
		[ 'April', null, 720 ],
		[ 'May', null, 744 ],
		[ 'June', null, 720 ],
		[ 'July', null, 744 ],
		[ 'August', null, 744 ],
		[ 'September', null, 720 ],
		[ 'October', null, 744 ],
		[ 'November', null, 720 ],
		[ 'December', null, 744 ],
		[ new Date(), null, 744 ]
	];

	for ( i = 0; i < values.length; i++ ) {
		expected = values[ i ][ 2 ];
		t.equal( hoursInMonth( values[ i ][ 0 ], values[ i ][ 1 ] ), expected, 'returns '+expected+' hours for '+values[ i ][ 1 ]+'/'+values[ i ][ 0 ] );
	}
	t.end();
});

